const headers = $input.first().json.headers || {};
const id_usuario = headers['id_usuario'];
//const id_empresa = $input.first().json.query.id

if (!id_usuario) {
  return [
    {
      json: {
        status: 'erro',
        mensagem: 'ID do usuário não informado. Por favor, verifique.',
        code: 400
      }
    }
  ];
}

return [
  {
    json: {
      status: 'ok',
      mensagem: 'ID do usuário recebido com sucesso.',
      id_usuario: id_usuario,
      code: 200
    }
  }
];

